<?phpclass home_public extends ObjectPublic{ 	function __construct(){            global $vsTemplate;            parent::__construct('products', CORE_PATH.'products/', 'products');                        $this->html = $vsTemplate->load_template('skin_home');      			}	function showDefault(){		global $vsSettings,$vsMenu,$bw,$vsTemplate,$DB,$vsStd,$vsLang;		$this->model->getNavigator();						$vsStd->requireFile(CORE_PATH."products/products.php");		$pr = new products();		$categories = $vsMenu->getCategoryGroup("products");      	$strIds = $vsMenu->getChildrenIdInTree($categories);      	$pr->setCondition("productCatId in ({$strIds}) and productStatus > 1");      	$pr->setOrder("productIndex ASC, productId DESC");      	$pr->setLimit(array(0,12));		$option['products'] = $pr->getObjectsByCondition();						$categories = $vsMenu->getCategoryGroup("quatang");      	$strIds = $vsMenu->getChildrenIdInTree($categories);      	$pr->setCondition("productCatId in ({$strIds}) and productStatus =2");      	$pr->setOrder("productIndex ASC, productId DESC");      	$pr->setLimit(array(0,9));		$option['quatang'] = $pr->getObjectsByCondition();										                 	return $this->output = $this->html->showDefault($option);	        	}				}?>